import React, { useEffect, useState } from 'react'
import axios from 'axios'

const PostList = () => {
 const [posts,SetPosts] = useState({});

 const fetchPosts = async () => {
     const res = await axios.request('http://localhost:4000/posts')
     SetPosts(res.data);
 }

 useEffect(() => {
     fetchPosts()
 },[])

  return (
    <div>PostList</div>
  )
}

export default PostList
